// https://opengraph.githubassets.com/f460d576b0a8eb3c0f96ab489be83adfbc198d418a5a38f168e5fc65008f7a4b/vercel/next.js
function Item({ title, subtitle, icon }) {
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'row',
        color: '#333',
        flexGrow: 1,
        textTransform: 'uppercase',
      }}
    >
      <span
        style={{
          fontFamily: 'Material Icons',
          fontSize: 20,
          marginRight: 5,
          marginTop: 2,
        }}
      >
        {icon}
      </span>
      <div style={{ display: 'flex', flexDirection: 'column' }}>
        <div style={{ color: '#000', fontSize: 20 }}>{title}</div>
        <div>{subtitle}</div>
      </div>
    </div>
  )
}

const VercelLogo =
  'data:image/png;base64,'

async function init() {
  if (typeof window === 'undefined') return []
  if (window.__initialized) return window.__initialized

  const [font, fontBold, fontIcon] = await Promise.all(
    (
      await Promise.all([
        fetch(
          'https://unpkg.com/@fontsource/inter@4.5.2/files/inter-latin-ext-400-normal.woff'
        ),
        fetch(
          'https://unpkg.com/@fontsource/inter@4.5.2/files/inter-latin-ext-700-normal.woff'
        ),
        fetch(
          'https://unpkg.com/@fontsource/material-icons@4.5.2/files/material-icons-base-400-normal.woff'
        ),
      ])
    ).map((res) => res.arrayBuffer())
  )

  return (window.__initialized = [
    {
      name: 'Inter',
      data: font,
      weight: 400,
      style: 'normal',
    },
    {
      name: 'Inter',
      data: fontBold,
      weight: 700,
      style: 'normal',
    },
    {
      name: 'Material Icons',
      data: fontIcon,
      weight: 400,
      style: 'normal',
    },
  ])
}

const waitForResource = init()

const width = 400 * 2
const height = 255 * 2

export default function Playground() {
  const [svg, setSvg] = useState('')
  let { repo, subtitle } = useRouter().query

  repo = repo || 'next.js ðŸ¤¯'
  subtitle = subtitle || 'the react framework âœ¨ðŸ¥°'

  const element = (
    <div
      style={{
        fontFamily: 'Inter',
        padding: 80,
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        flexGrow: 1,
      }}
    >
      <div
        style={{
          height: 100,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'flex-start',
          flexGrow: 1,
        }}
      >
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            flexGrow: 1,
            flexShrink: 1,
            flexBasis: 492,
          }}
        >
          <h1 style={{ fontSize: 56, fontWeight: 300, margin: 0 }}>
            vercel/<span style={{ fontWeight: 700 }}>{repo}</span>
          </h1>
          <p
            style={{
              fontSize: 24,
              color: '#666',
              textTransform: 'capitalize',
            }}
          >
            {subtitle}
          </p>
        </div>
        <div>
          <img
            width={148}
            height={148}
            src={VercelLogo}
            style={{
              borderRadius: 20,
            }}
          ></img>
        </div>
      </div>
      <div
        style={{
          display: 'flex',
          width: '100%',
          height: 53,
        }}
      >
        <Item title='2k' subtitle='Contributors' icon='&#xE7FB;' />
        <Item title='18k' subtitle='Used by' icon='&#xe0df;' />
        <Item title='9k' subtitle='Discussions' icon='&#xe0bf;' />
        <Item title='81k' subtitle='Stars' icon='&#xf06f;' />
        <Item title='17k' subtitle='Forks' icon='&#xebac;' />
      </div>
    </div>
  )

  useEffect(() => {
    ;(async () => {
      const emojiCodes = getTwemojiMap(subtitle + repo)
      const emojis = await Promise.all(
        Object.values(emojiCodes)
          .map(loadEmoji)
          .map((r) => r.then((res) => res.text()))
      )
      const graphemeImages = Object.fromEntries(
        Object.entries(emojiCodes).map(([key], index) => [
          key,
          `data:image/svg+xml;base64,` + btoa(emojis[index]),
        ])
      )

      const fonts = await waitForResource
      const result = satori(element, {
        width,
        height,
        fonts,
        graphemeImages,
        // embedFont: false,
        debug: true,
      })

      setSvg(result)
    })()
  }, [subtitle, repo])

  return (
    <div id='container'>
      <div id='svg' dangerouslySetInnerHTML={{ __html: svg }}></div>
      <div
        style={{
          position: 'relative',
          display: 'flex',
          flexDirection: 'row',
          flexWrap: 'wrap',
          width,
          height,
          overflow: 'hidden',
          letterSpacing: 0,
          fontSize: 16,
        }}
      >
        {element}
      </div>
    </div>
  )
}
