import { it, describe, expect } from 'vitest'

import { initFonts, toImage } from './utils.js'
import satori from '../src/index.js'

describe('Emojis', () => {
  let fonts
  initFonts((f) => (fonts = f))

  it('should detect emojis correctly', async () => {
    const emojis = []
    await satori(<div>‚õ∑üë®‚Äçüë©‚Äçüëß‚ù§Ô∏è‚Äçüî•üè≥Ô∏è‚Äçüåà„äóÔ∏èüõ†üë∂üèæ</div>, {
      width: 100,
      height: 100,
      fonts,
      loadAdditionalAsset: async (languageCode, segment) => {
        if (languageCode === 'emoji') {
          emojis.push(segment)
        }
        return 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPj/HwADBwIAMCbHYQAAAABJRU5ErkJggg=='
      },
    })
    expect(emojis).toMatchInlineSnapshot(`
      [
        "‚õ∑",
        "üë®‚Äçüë©‚Äçüëß",
        "‚ù§Ô∏è‚Äçüî•",
        "üè≥Ô∏è‚Äçüåà",
        "„äóÔ∏è",
        "üõ†",
        "üë∂üèæ",
      ]
    `)
  })

  // https://github.com/vercel/satori/issues/302
  it('should render emojis correctly', async () => {
    const svg = await satori(
      <div
        style={{
          backgroundColor: '#fff',
        }}
      >
        üåà üè≥Ô∏è‚Äçüåà Luci, üë®‚Äçüë®‚ÄçüëßNyanüë®‚Äçüë®‚Äçüëß‚Äçüëß
      </div>,
      {
        width: 200,
        height: 100,
        fonts,
        graphemeImages: {
          'üåà': 'data:image/svg+xml;base64,',
          'üè≥Ô∏è‚Äçüåà': 'data:image/svg+xml;base64,',
          'üë®‚Äçüë®‚Äçüëß': 'data:image/svg+xml;base64,',
          'üë®‚Äçüë®‚Äçüëß‚Äçüëß': 'data:image/svg+xml;base64,',
        },
      }
    )

    expect(await toImage(svg)).toMatchImageSnapshot()
  })

  it('should render emojis correctly with `word-break: break-all`', async () => {
    const svg = await satori(
      <div
        style={{
          backgroundColor: '#fff',
          wordBreak: 'break-all',
        }}
      >
        üåà üè≥Ô∏è‚Äçüåà Luci, üë®‚Äçüë®‚ÄçüëßNyanüë®‚Äçüë®‚Äçüëß‚Äçüëß
      </div>,
      {
        width: 200,
        height: 100,
        fonts,
        graphemeImages: {
          'üåà': 'data:image/svg+xml;base64,',
          'üè≥Ô∏è‚Äçüåà': 'data:image/svg+xml;base64,',
          'üë®‚Äçüë®‚Äçüëß': 'data:image/svg+xml;base64,',
          'üë®‚Äçüë®‚Äçüëß‚Äçüëß': 'data:image/svg+xml;base64,',
        },
      }
    )

    expect(await toImage(svg)).toMatchImageSnapshot()
  })
})
